package com.hackerrank

import org.scalatest.FunSuite

class SherlockAndValidStringTest extends FunSuite {

  test("testIsValid 1") {
    assertResult("NO")(SherlockAndValidString.isValid("aabbccddeefghi"))
  }

  test("testIsValid 2") {
    assertResult("NO")(SherlockAndValidString.isValid("aabbcd"))
  }

  test("testIsValid 3") {
    assertResult("YES")(SherlockAndValidString.isValid("abcdefghhgfedecba"))
  }

  test("testIsValid 4") {
    assertResult("YES")(SherlockAndValidString.isValid("abc"))
  }

  test("testIsValid 5") {
    assertResult("YES")(SherlockAndValidString.isValid(""))
  }

}
